
public class StringMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//split method
		String x ="hello word ";
		String y[] = x.split(" ");
		for (String s:y)
		{
			System.out.println(s);
		}
		
		//toUppercase method
		String z = x.toUpperCase();
		System.out.println(z);
		
		
		
	}

}
